<script>
export default {
  name: "loginAndSignUp",
  props: ["user", 'ddd'],
  data () {
    return {
      userAccount: "",
      userEmail: "",
      userPassword: "",
      isShow: true,
      new: {}
    };
  },
  methods: {
    lllll (data) {
      this.$emit("isShow", { display: 'none' })
      if (this.new)
        this.$emit("addNewAccount", data);
      this.userPassword = ''

      return this.isShow = !this.isShow
    },
    doRegister () {
      if (this.userAccount == "admin") {
        if (
          this.userEmail == this.user.admin[0].email &&
          this.userPassword == this.user.admin[0].password
        ) {
          this.isShow = !this.isShow
          this.$emit("isShow", {
            user: {
              account: "admin",
              email: this.user.admin[0].email,
              password: this.user.admin[0].password,
              role: "admin",
            },
          });
          this.userPassword = "";
        }
      } else {
        let flag = true;
        for (let i = 0; i < this.user.users.length; i++) {
          if (this.userAccount == this.user.users[i].account)
            if (
              this.userEmail == this.user.users[i].email &&
              this.userPassword == this.user.users[i].password
            ) {
              this.userPassword = "";
              this.isShow = !this.isShow
              this.$emit("isShow", {
                user: this.user.users[i],
              });
              flag = !flag;
            } else if (
              this.userEmail != this.user.users[i].email ||
              this.userPassword != this.user.users[i].password
            ) {
              return false;
            }
        }
        if (flag) {
          let data = {
            account: this.userAccount,
            password: this.userPassword,
            email: this.userEmail,
            role: "normal",
          };
          this.new = data
          this.isShow = !this.isShow
        }
      }
    },
  },
  watch: {
    ddd: function () {
      console.log(1)
    }
  }
};
</script>
 
